import './App.css';
import react,{ useState } from 'react';
import StepNavigarion from './stepnavigation';
function App() {
  const labelArray=['Step 1','Step 2','Step 3','Step 4','Step 5']
  const [currentStep,updateCurrentStep]=useState(1);


 function updateStep (step){
   updateCurrentStep(step);
 }



  return (
   <div>
     <StepNavigarion labelArray={labelArray} updateStep={updateStep} currentStep={currentStep}></StepNavigarion>
     <p>selected Step : {currentStep}</p>
     <button className='primarybutton'onClick={()=>updateStep(currentStep-1)}>Previous Step</button>
     <button className='primarybutton'onClick={()=>updateStep(currentStep+1)}>Next Step</button>


   </div>
  );
}

export default App;
